







--Update batch đata

DECLARE @cnt INT = 60;

WHILE @cnt < 2387
BEGIN
   update [dbo].Attachment Set Service_Id = (Select Id From ServiceDetails Where Code = (Select (SELECT value FROM STRING_SPLIT(Url, '/') Where value LIKE '%-%') From [dbo].[Attachment] where Id = @cnt)) Where Id = @cnt
   
   SET @cnt = @cnt + 1;
END;